use base64::{engine::general_purpose, Engine as _};
use ic_cdk::{export_candid, query};

mod http;
use http::{HttpRequest, HttpResponse};

const FAVICON: &str = "";

#[query]
async fn http_request(req: HttpRequest) -> HttpResponse {
    ic_cdk::println!("{:?}", req);
    if req.url == *"/favicon.ico" {
        return favicon_response();
    }

    HttpResponse {
        status_code: 200_u16,
        headers: vec![("Content-Type".to_string(), "text/plain".to_string())],
        body: "Hello".to_string().into_bytes(),
    }
}

fn favicon_response() -> HttpResponse {
    let body = general_purpose::STANDARD.decode(FAVICON).unwrap();
    ic_cdk::println!("favicon_response: {}", body.len());

    HttpResponse {
        status_code: 200_u16,
        headers: vec![
            (
                "Content-Type".to_string(),
                "image/vnd.microsoft.icon".to_string(),
            ),
            ("Content-Length".to_string(), body.len().to_string()),
        ],
        body,
    }
}

export_candid!();
